// Generated by CoffeeScript 1.4.0
(function() {

  App.pages.home = function($) {
    return App.modules.search($);
  };

  $('.ajax-pagination').delegate('a', 'click', function(e) {
    var content, self, url;
    self = $(this);
    url = self.attr('href');
    if (url.indexOf(App.baseurl) !== -1 || url.substr(0, 1) === '?') {
      e.preventDefault();
      content = self.closest('article').find('.content');
      return content.animate({
        opacity: 0.4
      }, function() {
        return $.get(url, {
          ajax: true
        }, function(response) {
          return content.html(response).animate({
            opacity: 1
          });
        });
      });
    }
  });

  App.modules.search = function($) {
    var s;
    s = $('#s');
    s.parent().submit(function(e) {
      return e.preventDefault();
    });
    return s.keypress(function(e) {
      if (13 === e.which) {
        return $.get('//netflixroulette.net/api/api.php', {
          actor: s.val()
        }, function(r) {
          return $.each(r, function(i, movie) {
            return console.log(movie.show_title);
          });
        });
      }
    });
  };

}).call(this);
